        if (!window.File || !window.FileReader || !window.FileList || !window.Blob) {
            alert('The File APIs are not fully supported in this browser.');
            return;
        }
        var file = this.files[0];
        var fileType = file.type;
        var match = ['image/jpeg', 'image/png', 'image/jpg'];
        if (!((fileType == match[0]) || (fileType == match[1]) || (fileType == match[2]) || (fileType == match[3]) || (fileType == match[4]) || (fileType == match[5]))) {
            alert('Sorry, only JPG, JPEG, & PNG files are allowed to upload.');
            // $(".inlineimg").val('');
            return false;
        }
    });
    $("#dkimlin").change(function () {
        if (!window.File || !window.FileReader || !window.FileList || !window.Blob) {
            alert('The File APIs are not fully supported in this browser.');
            return;
        }
        var file = this.files[0];
        var fileType = file.type;
        var match = ['text/plain'];
        if (!((fileType == match[0]) || (fileType == match[1]) || (fileType == match[2]) || (fileType == match[3]) || (fileType == match[4]) || (fileType == match[5]))) {
            alert('Sorry, only text(.txt) files are allowed to upload.');
            $("#dkimlin").val('');
            return false;
        }
    });
    $("#emlist").change(function () {
        if (!window.File || !window.FileReader || !window.FileList || !window.Blob) {
            alert('The File APIs are not fully supported in this browser.');
            return;
        }
        var file = this.files[0];
        var fileType = file.type;
        var match = ['text/plain'];
        if (!((fileType == match[0]) || (fileType == match[1]) || (fileType == match[2]) || (fileType == match[3]) || (fileType == match[4]) || (fileType == match[5]))) {
            alert('Sorry, only text(.txt) files are allowed to upload.');
            $("#emlist").val('');
            return false;
        }


        var input = document.getElementById('emlist');
        if (!input) {
            alert("Um, couldn't find the fileinput element.");
        }
        else if (!input.files) {
            alert("This browser doesn't seem to support the `files` property of file inputs.");
        }
        else if (!input.files[0]) {
            alert("Please select a file before clicking 'Load'");
        }
        else {
            var fileReader = new FileReader();
            fileReader.onload = function () {
                var data = fileReader.result;  // data <-- in this var you have the file data in Base64 format
                document.getElementById('prosList').value = data;
                var strArr = data.split("\n");
                document.getElementById("tEload").innerHTML = strArr.length;
                document.getElementById('tResults').value = "0";
                document.getElementById('tBAD').value = "0";

            };
            fileReader.readAsText($('#emlist').prop('files')[0]);
        }

    });


</script>

<script>
    function start() {

        var emailList = document.getElementById('prosList').value;
        var coma = document.getElementById('coma').value;
        var set_async = document.getElementById('push_type').value;

        var strArr = emailList.split("\n");
        var data = "";
        var rstatus = "";
        var emcount = strArr.length;


        if (emailList == '') {
            $(".alert-message").show(500);
            document.getElementById("checkinginfo").innerHTML = "Email list can not be empty!";
        } else
            if (coma == '') {
                $(".alert-message").show(500);
                document.getElementById("checkinginfo").innerHTML = " Please login again and check your authorisation status!";
            } else
                if (coma != 'codespirit') {
                    $(".alert-message").show(500);
                    document.getElementById("checkinginfo").innerHTML = "Please contact codespirit for approval!";
                } else {
                    $(".alert-message").hide(500);
                    $(".checking").show(200);

                    document.getElementById("tEload").innerHTML = emcount;
                    document.getElementById("tecount").innerHTML = emcount;

                    var regoodc = document.getElementById("regood").innerHTML;
                    var rebadc = document.getElementById("rebad").innerHTML;

                    var tResults = document.getElementById("tResults").innerHTML;
                    var tBAD = document.getElementById("tBAD").innerHTML;

                    if (set_async == "false") {
                        for (var i = 0; i < emcount; i++) {
                            const formData = new FormData($('#xSpiritForm')[0]);
                            formData.append("action", "action");
                            formData.append("emlistx", strArr[i]);

                            $.ajax({
                                url: '.prog.php',
                                async: true,
                                type: 'POST',
                                dataType: 'json',
                                data: formData,
                                processData: false,
                                cache: false,
                                timeout: 60000000,
                                contentType: false,
                                success: function (result) {

                                    if (result.status == "R00") {
                                        regoodc++

                                        document.getElementById("tResults").innerHTML = regoodc;
                                    } else
                                        if (result.status == "R12") {
                                            rebadc++

                                            document.getElementById("tBAD").innerHTML = rebadc;
                                        }

                                    if (emcount <= i) {
                                        document.getElementById("checkdone").innerHTML = "ALL HAS BEEN SENT";
                                    }

                                    document.getElementById("ecount").innerHTML = i;
                                    document.getElementById("prosList").innerHTML = rstatus;

                                    document.getElementById("tBAD").innerHTML = rebadc;
                                    document.getElementById("rebad").innerHTML = rebadc;
                                    document.getElementById("tResults").innerHTML = regoodc;
                                    document.getElementById("regood").innerHTML = regoodc;

                                },
                                error: function (er) {
                                    rebadc++
                                    document.getElementById("tBAD").innerHTML = rebadc;
                                    $(".alert-message").show(500);
                                    document.getElementById("checkinginfo").innerHTML = "Sorry, I could not process the request, contact COde SPirit.";
                                }

                            });
                        }
                    } else {
                        const formData = new FormData($('#xSpiritForm')[0]);
                        formData.append("action", "action");
                        $.ajax({
                            url: '.prog.php',
                            async: true,
                            type: 'POST',
                            dataType: 'json',
                            data: formData,
                            processData: false,
                            cache: false,
                            timeout: 60000000,
                            contentType: false,
                            success: function (result) {

                                if (result.status == "R00") {
                                    document.getElementById("tResults").innerHTML = result.count;
                                } else
                                    if (result.status == "R12") {
                                        document.getElementById("tBAD").innerHTML = result.count;
                                    }

                                if (emcount = result.count) {
                                    document.getElementById("checkdone").innerHTML = "ALL HAS BEEN SENT";
                                }

                                document.getElementById("ecount").innerHTML = result.count;
                                document.getElementById("prosList").innerHTML = rstatus;

                                document.getElementById("tBAD").innerHTML = rebadc;
                                document.getElementById("rebad").innerHTML = rebadc;
                                document.getElementById("tResults").innerHTML = result.count;
                                document.getElementById("regood").innerHTML = result.count;

                            },
                            error: function (er) {

                                document.getElementById("tBAD").innerHTML = emcount;
                                $(".alert-message").show(500);
                                document.getElementById("checkinginfo").innerHTML = "Sorry, I could not process the request, contact COde SPirit.";
                            }

                        });
                    }

                }



    }
</script>
</body>

